type IconProps = {
    className?: string;
    name: string;
    fill?: string;
};

const Icon = ({ className, name, fill }: IconProps) => {
    // Check if className contains a fill-* class (but not fill-inherit or fill-current)
    const hasFillClass = className && /fill-(?!inherit|current)/.test(className);
    
    return (
        <svg
            className={`inline-flex size-6 ${hasFillClass ? '' : 'fill-current'} ${className || ""}`}
            width={24}
            height={24}
            viewBox="0 0 24 24"
            style={fill ? { fill } : undefined}
        >
            <path d={icons[name]} />
        </svg>
    );
};

export default Icon;
